    $(document).ready(function() {
    $('.item .js').click(function() {
    let name = $(this).data('name');
    let description = $(this).data('description');
    let image = $(this).data('image');
    let id = $(this).siblings('p').text();

    $('#exampleModalLabel').text(name);
    $('#modal-img').attr('src', image);
    $('#modal-description').text(description);
    $('#modal-button').text('ID: ' + id);
    $.ajax({
      type: "POST",
      url: "my_page.php",
      data: { id: id }
    }).done(function(response) {
      

const buttonValues = response.split(';');
const buttonCount = buttonValues[0];
const buttonData = buttonValues.slice(1);

const buttons = [];

for (let i = 0; i < buttonData.length; i++) {
  const buttonInfo = buttonData[i].split(',');
  const buttonName = buttonInfo[0];
  const buttonLink = buttonInfo[1];
  
  const button = {
    name: buttonName,
    link: buttonLink
  };
  
  buttons.push(button);
}

let buttonHTML = "";

for (let i = 0; i < buttons.length; i++) {
  const buttonName = buttons[i].name;
  const buttonLink = buttons[i].link;
  
  buttonHTML += "<a href='" + buttonLink + "' class='btn btn-primary'>" + buttonName + "</a>&nbsp;";
}


$('#modal-button').html(buttonHTML);
    });
  });
});
